import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

function CourseList() {
  const [courses, setCourses] = useState([]);
  const [filter, setFilter] = useState("");

  useEffect(() => {
    fetch("http://localhost:5000/api/courses")
      .then(res => res.json())
      .then(data => setCourses(data))
      .catch(err => console.error(err));
  }, []);

  const filteredCourses = filter
    ? courses.filter(course => course.skills.includes(filter))
    : courses;

  const uniqueSkills = [...new Set(courses.flatMap(course => course.skills))];

  return (
    <div style={{ maxWidth: 800, margin: "auto", padding: 20 }}>
      <h2>Courses</h2>
      <select value={filter} onChange={e => setFilter(e.target.value)}>
        <option value="">All Skills</option>
        {uniqueSkills.map(skill => (
          <option key={skill} value={skill}>{skill}</option>
        ))}
      </select>
      <ul>
        {filteredCourses.map(course => (
          <li key={course._id} style={{ margin: "15px 0" }}>
            <Link to={`/courses/${course._id}`}>
              <h3>{course.title}</h3>
            </Link>
            <p>{course.description}</p>
            <p><b>Skills:</b> {course.skills.join(", ")}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default CourseList;
