import React, { useEffect, useState } from "react";
import "./DashBoard.css";

function Dashboard() {
  const [courses, setCourses] = useState([]);
  const username = "john_doe"; // You can make this dynamic if needed

  useEffect(() => {
    fetch("http://localhost:5000/api/courseRoutes")
      .then((res) => res.json())
      .then((data) => setCourses(data))
      .catch((err) => console.error("Error fetching courses:", err));
  }, []);

  const handleSelect = async (id) => {
    try {
      const res = await fetch(`http://localhost:5000/api/courseRoutes/${id}/select?username=${username}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
      });

      const data = await res.json();
      if (res.ok) {
        alert("‚úÖ You have selected the course!");
      } else {
        alert(`‚ö†Ô∏è ${data.message}`);
      }
    } catch (err) {
      console.error("Error selecting course:", err);
      alert("‚ùå Something went wrong. Try again.");
    }
  };

  return (
    <div className="dashboard container mt-4">
      <h2 className="mb-4 text-center">üìö Featured Courses</h2>
      <div className="row">
        {courses.map((course) => (
          <div className="col-md-4 mb-4" key={course._id}>
            <div className="card h-100 shadow-sm">
              <div className="card-body">
                <h5 className="card-title">{course.title}</h5>
                <p className="card-text">{course.description}</p>
                <span className="badge bg-primary mb-2">{course.level}</span>
                <br />
                <button className="btn btn-success mt-2" onClick={() => handleSelect(course._id)}>
                  Select Course
                </button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Dashboard;
